import React from 'react';
import {Card, CardContent, Typography, Grid, CircularProgress} from '@material-ui/core';
import CountUp from 'react-countup';
import cx from 'classnames';

import styles from './CardsStyle.css';


const Cards = ({ data: {confirmed, recovered, deaths, lastUpdate} }) => {
    if(!confirmed){
        return(
            <CircularProgress color="secondary" />
        )
    }
    return(
        <div className= {styles.container}>
            <Grid container spacing={2} justify='center'>
                <Grid item component={Card} xs={12} md={3} className={cx(styles.card, styles.infected)}>
                    <CardContent>
                        <Typography variant='h4' gutterBottom>Infected</Typography>
                        <Typography variant='h5'>
                            <CountUp 
                                start={0}
                                end={confirmed.value}
                                duration={2.0}
                                separator=","
                            />
                        </Typography>
                        <Typography variant='body2'>Number of people infected with COVID19 till</Typography>
                        <Typography color='textSecondary'>{new Date(lastUpdate).toDateString()}</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card} xs={12} md={3} className={cx(styles.card, styles.recover)}>
                    <CardContent>
                        <Typography variant='h4' gutterBottom>Recovered</Typography>
                        <Typography variant='h5'>
                        <CountUp 
                                start={0}
                                end={recovered.value}
                                duration={2.0}
                                separator=","
                                />
                        </Typography>
                        <Typography variant='body2'>Number of people recovered from COVID19 till</Typography>
                        <Typography color='textSecondary'>{new Date(lastUpdate).toDateString()}</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card} xs={12} md={3} className={cx(styles.card, styles.death)}>
                    <CardContent>
                        <Typography variant='h4' gutterBottom>Deaths</Typography>
                        <Typography variant='h5'>
                        <CountUp 
                                start={0}
                                end={deaths.value}
                                duration={2.0}
                                separator=","
                            />
                        </Typography>
                        <Typography variant='body2'>Number of deaths due to COVID19 till</Typography>
                        <Typography color='textSecondary'>{new Date(lastUpdate).toDateString()}</Typography>
                    </CardContent>
                </Grid>
            </Grid>
        </div>
    );
}

export default Cards
